CREATE DATABASE `gdce`;
USE `gdce`;

DROP TABLE IF EXISTS `system_users`;

CREATE TABLE `system_users` (
  `id` int NOT NULL AUTO_INCREMENT,
  `NAME` varchar(20) NOT NULL COMMENT '用户名称',
  `PASSWORD` varchar(20) NOT NULL COMMENT '密码',
  `LEVEL` int(2) NOT NULL COMMENT '0-超管 1-管理员 2-会员',
  `permission` varchar(10) NOT NULL COMMENT '权限（0-创建管理员 1-买方 2-卖方 3-物流 4-资讯编辑 5-资讯审核 6-注册审核 7-交易审核 8-广告位审核）',
  `companyId` varchar(30) NOT NULL COMMENT '企业机构代码',
  `isRemember` tinyint(1) NOT NULL COMMENT '记住我',
  `loginState` tinyint(1) NOT NULL COMMENT '登录状态（true-在线 false-离线）',
  `username` varchar(10) NOT NULL COMMENT '管理员姓名',
  `department` varchar(20) NOT NULL COMMENT '管理员部门',
  `phonenumber` varchar(20) NOT NULL COMMENT '管理员联系电话',
  `email` varchar(30) NOT NULL COMMENT '管理员邮箱',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=12 DEFAULT CHARSET=utf8;

/*Data for the table `system_users` */



/*Table structure for table `table_accountant_info` */

DROP TABLE IF EXISTS `table_accountant_info`;

CREATE TABLE `table_accountant_info` (
  `id` int(4) NOT NULL AUTO_INCREMENT,
  `NAME` varchar(10) NOT NULL COMMENT '会计姓名',
  `phone` varchar(11) NOT NULL COMMENT '会计电话',
  `email` varchar(30) NOT NULL COMMENT '会计邮箱',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=8 DEFAULT CHARSET=utf8;

/*Data for the table `table_accountant_info` */


/*Table structure for table `tbl_ads` */

DROP TABLE IF EXISTS `tbl_ads`;

CREATE TABLE `tbl_ads` (
  `id` int(4) NOT NULL AUTO_INCREMENT,
  `companyId` varchar(30) NOT NULL COMMENT '企业机构代码',
  `POSITION` int(1) NOT NULL COMMENT '广告位 1-4',
  `email` varchar(30) NOT NULL COMMENT '邮箱',
  `adsImgPath` varchar(45) NOT NULL COMMENT '广告图片存放路径（/imgs/ads/企业机构代码+时间戳）',
  `adsURL` varchar(100) NOT NULL COMMENT '广告超链地址',
  `note` varchar(200) NOT NULL COMMENT '备注',
  `releaseDate` varchar(20) NOT NULL COMMENT '广告投放时间',
  `endDate` varchar(20) NOT NULL COMMENT '广告截止时间',
  `price` double(10,2) NOT NULL COMMENT '广告费用',
  `reviewState` int(3) NOT NULL COMMENT '0-待审核 1-通过 2-未通过',
  `recordID` varchar(20) NOT NULL COMMENT '记录编号',
  `submitDate` varchar(20) NOT NULL COMMENT '提交审核时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=utf8;

/*Data for the table `tbl_ads` */


/*Table structure for table `tbl_company_info` */

DROP TABLE IF EXISTS `tbl_company_info`;

CREATE TABLE `tbl_company_info` (
  `id` int NOT NULL AUTO_INCREMENT,
  `companyId` varchar(30) NOT NULL COMMENT '企业机构代码',
  `companyName` varchar(30) NOT NULL COMMENT '企业名称',
  `legalPerson` varchar(10) NOT NULL COMMENT '法人',
  `legalPersonId` varchar(18) NOT NULL COMMENT '法人省份证号',
  `accountantName` varchar(10)  NULL COMMENT '会计姓名',
  `companyAddress` varchar(30)  NULL COMMENT '公司注册地址',
  `companyEmail` varchar(20) NOT NULL COMMENT '公司邮箱',
  `phoneNumber` varchar(20) NOT NULL COMMENT '联系方式',
  `companyFax` varchar(10) NOT NULL COMMENT '传真',
  `postalCode` varchar(8) NOT NULL COMMENT '邮政编码',
  `registerMoney` float(12,2) NULL COMMENT '注册资金',
  `openLicenseId` varchar(30) NULL COMMENT '营业执照',
  `bussionLicense` varchar(30) NULL COMMENT '经营许可证',
  `taxRegisterId` varchar(30) NULL COMMENT '税务登记号',
  `openBank` varchar(20) NULL COMMENT '开户银行',
  `bankAccount` varchar(30) NULL COMMENT '银行账户',
  `goodsAddress` varchar(40) NULL COMMENT '煤源存放地',
  `transportStyle` varchar(15) NULL COMMENT '运输方式保证能力',
  `companyProfile` varchar(300) NULL COMMENT '公司简介',
  `openLicensePath` varchar(60)  NULL COMMENT '营业执照存放路径（/imgs/openLicense/营业执照+时间戳）',
  `taxRegisterCodePath` varchar(60) NULL COMMENT '税务登记证照片存放路径（/imgs/taxRegisterCode/税务登记证+时间戳）',
  `companyCodePath` varchar(60) NULL COMMENT '组织机构代码照片存放路径（/imgs/companyCode/企业机构代码+时间戳）',
  `bankAccountCodePath` varchar(60) NULL COMMENT '开户银行许可证照片存放路径（/imgs/bankAccountCode/开户银行许可证+时间戳）',
  `legalPersonCodePath` varchar(60) NULL COMMENT '法人身份证照片存放路径（/imgs/legalPersonCode/法人身份证+时间戳）',
  `bussionLicenseCodePath` varchar(60) NULL COMMENT '经营许可证照片存放路径（/imgs/bussionLicenseCode/经营许可证+时间戳）',
  `reviewState` int(3) NOT NULL COMMENT '0-待审核 1-通过 2-未通过',
  `recordID` varchar(20) NULL COMMENT '记录编号',
  `submitDate` varchar(20) NULL COMMENT '提交审核时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=8 DEFAULT CHARSET=utf8;

/*Data for the table `tbl_company_info` */



/*Table structure for table `tbl_contract_info` */

DROP TABLE IF EXISTS `tbl_contract_info`;

CREATE TABLE `tbl_contract_info` (
  `id` int(4) NOT NULL AUTO_INCREMENT,
  `contractId` int(20) NOT NULL COMMENT '合同编号',
  `buyerCompanyId` varchar(30) NOT NULL COMMENT '买方编号（对应企业编号）',
  `sellerCompanyId` varchar(30) NOT NULL COMMENT '卖方编号（对应企业编号）',
  `productId` int(10) NOT NULL COMMENT '商品编号',
  `buyerContractState` tinyint(1) NOT NULL COMMENT '买方合同状态(true-已签 false-未签)',
  `sellerContractState` tinyint(1) NOT NULL COMMENT '卖方合同状态(true-已签 false-未签)',
  `createDate` varchar(20) NOT NULL COMMENT '创建时间',
  `endDate` varchar(20) NOT NULL COMMENT '结束时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=20 DEFAULT CHARSET=utf8;

/*Data for the table `tbl_contract_info` */


/*Table structure for table `tbl_logistics_info` */

DROP TABLE IF EXISTS `tbl_logistics_info`;

CREATE TABLE `tbl_logistics_info` (
  `id` int(4) NOT NULL AUTO_INCREMENT,
  `waybillId` varchar(20) NOT NULL COMMENT '运单号（时间戳+物流商企业机构代码编号后四位：2018080110034455）',
  `updateDate` varchar(20) NOT NULL COMMENT '更新时间',
  `submitUser` varchar(5) NOT NULL COMMENT '提交人',
  `location` varchar(20) NOT NULL COMMENT '地点',
  `content` varchar(80) NOT NULL COMMENT '内容',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8;

/*Data for the table `tbl_logistics_info` */


/*Table structure for table `tbl_news_info` */

DROP TABLE IF EXISTS `tbl_news_info`;

CREATE TABLE `tbl_news_info` (
  `id` int(4) NOT NULL AUTO_INCREMENT,
  `NAME` varchar(20) NOT NULL COMMENT '用户名称',
  `title` varchar(50) NOT NULL COMMENT '标题',
  `TYPE` varchar(10) NOT NULL COMMENT '类型（电力行业、煤炭价格、市场分析、宏观政策）',
  `reviewState` int(3) NOT NULL COMMENT '0-待审核 1-通过 2-未通过',
  `createDate` varchar(20) NOT NULL COMMENT '创建时间',
  `content` varchar(1000) NOT NULL COMMENT '内容',
  `recordID` varchar(20) NOT NULL COMMENT '记录编号',
  `submitDate` varchar(20) NOT NULL COMMENT '提交审核时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=31 DEFAULT CHARSET=utf8;

/*Data for the table `tbl_news_info` */


/*Table structure for table `tbl_order_info` */

DROP TABLE IF EXISTS `tbl_order_info`;

CREATE TABLE `tbl_order_info` (
  `id` int(4) NOT NULL AUTO_INCREMENT,
  `orderId` varchar(20) NOT NULL COMMENT '订单号(时间戳+卖家企业机构代码编号后四位：2018080110034455)',
  `tradePrice` float(8,2) NOT NULL COMMENT '交易金额',
  `productId` int(4) NOT NULL COMMENT '商品编号',
  `buyerCompanyId` varchar(30) NOT NULL COMMENT '买方编号（对应企业编号）',
  `sellerCompanyId` varchar(30) NOT NULL COMMENT '卖方编号（对应企业编号）',
  `orderState` int(2) NOT NULL COMMENT '订单状态（0-待付款 1-待发货 2-待收货 3-待验收 4-完成）',
  `receiptPath` varchar(45) NOT NULL COMMENT '回执单存放路径（/imgs/order/receipt/买家编号（后四位）+订单编号',
  `createDate` varchar(20) NOT NULL COMMENT '创建时间',
  `endDate` varchar(20) NOT NULL COMMENT '结束时间（默认15个工作日）',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=18 DEFAULT CHARSET=utf8;

/*Data for the table `tbl_order_info` */


/*Table structure for table `tbl_product_info` 交易*/

DROP TABLE IF EXISTS `tbl_product_info`;

CREATE TABLE `tbl_product_info` (
  `id` int(4) NOT NULL AUTO_INCREMENT,
  `productId` int(6) NOT NULL COMMENT '商品编号（从1开始）',
  `productState` int(1) NOT NULL COMMENT '商品状态（0-上架 1-下架 2-锁定）',
  `quantity` float(5,2) NOT NULL COMMENT '数量（万吨）',
  `lowPower` int(5) NOT NULL COMMENT '热值',
  `price` float(5,2) NOT NULL COMMENT '原煤单价（元/吨）',
  `fullSulfur` float(5,2) NOT NULL COMMENT '全硫份',
  `srcAddress` varchar(20) NOT NULL COMMENT '产地（山西、内蒙）',
  `carriage` float(5,2) NOT NULL COMMENT '运费单价',
  `volatiles` varchar(5) NOT NULL COMMENT '挥发份(数据格式：30-40)',
  `portAddress` varchar(20) NOT NULL COMMENT '发货港口()',
  `dryPowder` float(5,2) NOT NULL COMMENT '空干基灰分',
  `fullMoisture` float(5,2) NOT NULL COMMENT '全水分',
  `userName` varchar(20) NOT NULL COMMENT '用户名称',
  `reviewState` int(3) NOT NULL COMMENT '0-待审核 1-通过 2-未通过',
  `OffProductInfo` varchar(100) COMMENT '商品下架原因',
  `recordID` varchar(20) NOT NULL COMMENT '记录编号',
  `submitDate` varchar(20) NOT NULL COMMENT '提交审核时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=42 DEFAULT CHARSET=utf8;

/*Data for the table `tbl_product_info` */


/*Table structure for table `tbl_static_logistics_price` */

DROP TABLE IF EXISTS `tbl_static_logistics_price`;

CREATE TABLE `tbl_static_logistics_price` (
  `id` int(4) NOT NULL AUTO_INCREMENT,
  `waybillCompanyId` varchar(30) NOT NULL COMMENT '企业机构代码',
  `startingPlace` varchar(20) NOT NULL COMMENT '起始地',
  `destination` varchar(20) NOT NULL COMMENT '目的地',
  `weight` varchar(10) NOT NULL COMMENT '重量（>=3000）万吨',
  `price` int(4) NOT NULL COMMENT '价格',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=15 DEFAULT CHARSET=utf8;

/*Data for the table `tbl_static_logistics_price` */


/*Table structure for table `tbl_waybill_info` */

DROP TABLE IF EXISTS `tbl_waybill_info`;

CREATE TABLE `tbl_waybill_info` (
  `id` int(4) NOT NULL AUTO_INCREMENT,
  `waybillId` varchar(20) NOT NULL COMMENT '运单号（时间戳+物流商企业机构代码编号后四位：2018080110034455）',
  `waybillCompanyId` varchar(30) NOT NULL COMMENT '企业机构代码',
  `createTime` varchar(20) NOT NULL COMMENT '创建时间',
  `endTime` varchar(20) NOT NULL COMMENT '结束时间（用户收货后更新）',
  `orderId` varchar(20) NOT NULL COMMENT '订单号(2018080110034455)',
  `waybillState` varchar(2) NOT NULL COMMENT '运单状态（0-待发货 1-运输中 2-待签收 3-已签收）',
