DROP TABLE IF EXISTS `tbl_operate_record`;

CREATE TABLE `tbl_operate_record` (
  `id` int PRIMARY KEY AUTO_INCREMENT COMMENT '自增ID',
  `recordID` varchar(20) NOT NULL COMMENT '记录编号',
  `userName` varchar(20) NOT NULL COMMENT '操作者账号',
  `operateType` int NOT NULL COMMENT '操作类型（0：通过，1：不通过，2：撤销通过，3：下架）',
